INSERT INTO `a_acaricides` (`id`, `crop_id`, `product`, `productId`, `dose`, `note`, `afterNote`, `minimumUse`, `disease`, `quarantine`, `category`, `practices`, `isActive`) VALUES
(1, 18, 'АПОЛО 50 СК', 1, '40 мл/дка', NULL, NULL, NULL, 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', '3', 2, NULL, 0),
(2, 21, 'АПОЛО 50 СК', 1, '30-40 мл/дка', 'винени сортове', NULL, NULL, 'Жълт лозов акар (<span class="latin_name">Schizotetranychus viticola</span>)', '30', 2, '<h3 >Жълт лозов акар - <span class="latin_name">Schizotetranychus viticola</span></h3>\r\n            <p>\r\n                Развива до 9-10 поколения годишно и зимува като оплодени женски индивиди, събрани по много \r\n                заедно, под кората на лозата. Миграцията на презимувалите акари към развиващи се пъпки \r\n                започва към средата и началото на втората половина на април. Те се хранят със сок и \r\n                хлорофил от развиващите се листа и в края на април или началото на май започват да снасят \r\n                яйца по долната страна на листата. Излюпването на яйцата започва през първото десетдневие на \r\n                май и възрастни акари от първо поколение се появяват към средата и началото на втората \r\n                половина на май. През юни - септември развитието протича интензивно и е зависимо основно от \r\n                сорта и температурата на въздуха.\r\n            </p>\r\n            <p>\r\n                Повредените листа променят оцветяването и консистенцията си - стават жълто-зелени, \r\n                ръждивокафяви или червеникави, с по- груба и хрупкава при стриване листна петура. Вредната \r\n                дейност се отразява неблагоприятно върху протичане на физиологични и биохимични процеси в \r\n                листата, в резултат на което лозите се изтощават, дават некачествено и малко по количество грозде.\r\n            </p>\r\n<h4 class="my_class_h4">Стратегия за борба</h4>\r\n            <p>\r\n                От всички видове акари икономическо значение като неприятели на лозата имат жълтият лозов акар \r\n                и червеният овощен акар. Акарите повреждат най-силно листата на сортовете с по-дебел палисаден \r\n                паренхим - Памид, Димят, Ркацители, Каберне Совиньон, Мерло и др. По-слабо се повреждат \r\n                сортовете Болгар, Супер ран Болгар, Италия и др.\r\n            </p>\r\n            <p>\r\n                Всички видове вредни акари имат много естествени врагове - акарофаги - хищни акари, хищни \r\n                дървеници, златоочици, калинки и др., които осъществяват значително и пълно биологично \r\n                регулиране на популациите на акарите. Познаването на този факт е изключително важно, тъй \r\n                като елиминира необходимостта от използване на акарициди, а това се налага само при \r\n                достигане праговете на икономическа вредност.\r\n            </p>', 0),
