INSERT INTO `doses` (`id`, `pesticides_id`, `dose`, `doseNote`, `secondDose`, `measure`, `measureId`, `note`, `afterNote`, `crop`, `disease`, `application`, `quarantine`, `isCalc`, `isActive`) VALUES
(1, 1, '40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Домати:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 21 Видима поява на първо връхно разклонение До: Съобразно карантинния срок от 3 дни.', '3', 1, 0),
(2, 1, '30-40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Лозя – винени сортове:', 'Жълт лозов акар (<span class="latin_name">Schizotetranychus viticola</span>)', 'От ВВСН 11 Първият лист е отворен и се разстила настрани от летораста До: съобразно карантинния срок от 35 дни.', '30', 0, 0),
(3, 1, '40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Круши:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 00 Покой: листните пъпки и по-дебелите цветни пъпки са затворени и покрити с дебели тъмни люспи. До: съобразно карантинния срок от 35 дни.', '35', 1, 0),
(4, 1, '30-40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Краставици:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 21 Поява на първо странично разклонение. До: съобразно карантинния срок', '3', 0, 0),
(5, 1, '30-40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Пъпеш:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 21 Поява на първо странично разклонение. До: съобразно карантинния срок от 3 дни.', '3', 0, 0),
(6, 1, '30-40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Ягоди:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 10 Поява на първи лист. До: съобразно карантинния срок от 3 дни.', '3', 0, 0),
(7, 1, '30-40', NULL, NULL, 'мл/дка', 1, NULL, NULL, 'Малини:', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'От ВВСН 15 Разтворен пети лист. До: съобразно карантинния срок от 7 дни.', '7', 0, 0),
(8, 2, '0,1', NULL, NULL, '%', 3, NULL, NULL, 'Зеленчуци', 'Обикновен паяжинообразуващ акар по зеленчуци, Атлантически акар', NULL, '21', 1, 0),
(9, 3, '25-100', NULL, '(50-100 мл/хл)', 'мл/дка', 1, NULL, NULL, 'Лозя (винени сортове)', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'При появата на първите подвижни форми на акарите', '10', 0, 0),
(10, 3, '50-120', NULL, '(50-100 мл/хл)', 'мл/дка', 1, NULL, NULL, 'Лозя (десертни сортове)', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'При появата на първите подвижни форми на акарите', '10', 0, 0),
(11, 3, '15-100', NULL, '(50-100 мл/хл)', 'мл/дка', 1, NULL, NULL, 'Ягоди', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranichus urticae</span>)<br/>\r\nЯгодов (цикламов) акар (<span class="latin_name">Phytonemus pallidus</span>)<br/>\r\nЛистоминиращи мухи (<span class="latin_name">Lyriomyza spp.</span>)', 'При появата на първите подвижни форми на акарите.\r\nПри появата на първите снесени яйца при листоминиращите мухи.', '3', 0, 0),
(12, 3, '15-100', NULL, '(50-100 мл/хл)', 'мл/дка', 1, NULL, NULL, 'Домати (полско и оранжерийно производство)', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranichus urticae</span>)<br/>\r\nСребрист цитрусов акар (<span class="latin_name">Polyphagotarsoneumus latus</span>)<br/>\r\nДоматен акар (<span class="latin_name">Aculus lycopersici</span>)-при оранжерийно производство)<br/>\r\nЛистоминиращи мухи (<span class="latin_name">Lyriomyza spp.</span>)', 'При появата на първите подвижни форми на акарите.\r\nПри появата на първите снесени яйца при листоминиращите мухи.', '3', 0, 0),
(13, 3, '15-80', NULL, '(50-80 мл/хл)', 'мл/дка', 1, NULL, NULL, 'Патладжан (полско и оранжерийно производство)', 'Обикновен паяжинообразуващ акар (<span class="latin_name">Tetranychus urticae</span>)', 'При появата на първите подвижни форми на акарите.', '3', 0, 0),
