INSERT INTO `pesticides` (`id`, `name`, `type`, `moreNames`, `secondName`, `manufacturersId`, `firmName`, `permission`, `valid`, `dateOrder`, `period`, `substance`, `lethal`, `category`, `alphabet`, `pesticide`, `pesticideId`, `pestDescription`) VALUES
(1, 'АПОЛО 50 СК', 'СК - суспензионен концентрат', NULL, NULL, 1, 'АДАМА Ирвита Н.В.', 'Разрешеине № 94- ПРЗ -3 / 07.06.2016 г.', '30.04.2020 г.', NULL, '*Гратисен период 6 месеца за продажба и още 1 година за съхранение и употреба, считано от 30.04.2016 г. на ПРЗ -Удостоверение № 94/21.01.2004 г.', '500 г/л клофентезин', '3200', '2', 1, 'акарицид', 1, 'АПОЛО 50 СК - Акарицид при Домати, Лозя, Круши, Краставици, Пъпеш, Ягоди и Малини срещу Обикновен паяжинообразуващ акар (Tetranychus urticae) и Жълт лозов акар (Schizotetranychus viticola)'),
(2, 'БИ-58', 'ЕК - емулсионен концентрат', NULL, NULL, 2, 'БАСФ', 'Удостоверение № 79/23.07.2003 г.', NULL, 'РД 11-1243/14.06.2017 г.', NULL, '400 г/л диметоат', '150', '3', 2, 'акарицид', 1, 'БИ-58 Инсектицид и Акарицид за борба срещу Обикновен паяжинообразуващ акар по зеленчуци и Атлантически акар по зеленчуци.'),
(3, 'ВАЛМЕК', 'ЕК - емулсионен концентрат', NULL, NULL, 3, 'Индустриас Афраза', 'Разрешение № 01418 – ПРЗ- 1/07.06.2016 г.', '30.04.2020', 'РД 11- 950/31.05.2016 г.', NULL, 'абамектин 18 г/л', NULL, '2', 3, 'акарицид', 1, 'ВАЛМЕК акарицид/инсектицид  при Лозя, Домати, Ягоди, Патладжани, Маруля, Салати, Краставици, Корнишони, Тиквички, Праскови, Ябълка и Круша срещу Обикновен паяжинообразуващ акар (Tetranychus urticae), Листоминиращи мухи, Червен овощен акар, Листни бълхи, Доматен акар и др.'),
(4, 'ВЕРТИМЕК 018 ЕК', 'ЕК - емулсионен концентрат', NULL, NULL, 4, 'Синджента', 'Удостоверение № 01222-ПРЗ /21.02.2014 г.', '21.02.2024 г.', NULL, NULL, '18 г/л абамектин', NULL, '2', 3, 'акарицид', 1, 'ВЕРТИМЕК 018 ЕК Акарицид и Инсектицид при ябълки, домати на открито и в оранжерии, краставици и пипер в оранжерии срещу червен овощен акар, листоминиращи мухи и паяжинообразуващ акар.'),
(5, 'ВОЛИАМ ТАРГО 063 СК', 'СК - суспензионен концентрат', NULL, NULL, 4, 'Синджента', 'Удостоверение № 01233– ПРЗ/.2014 г.', '28.03.2017 г.', NULL, NULL, 'хлорантранилипрол 45 г/л + абамектин 18 г/л', NULL, '2', 3, 'акарицид', 1, 'ВОЛИАМ ТАРГО 063 СК Инсектицид и Акарицид при ябълки срещу червен овощен акар и ябълков плодов червей и при домати срещу доматен миниращ молец.'),
(6, 'ДИАБЛО', 'ВП - намокрим прах', NULL, NULL, 3, 'Индустриас Афраза', 'Разрешение № 01493- ПРЗ-1/ 20.07.2017 г.', '31.05.2022 г.', 'РД 11- 1363/26.06.2017', NULL, 'хекситиазокс 100 г/кг', NULL, '2', 5, 'акарицид', 1, 'ДИАБЛО Акарицид при лозя (винени и десертни сортове), семкови овощни видове (ябълка и круша) и цитрусови култури (портокали, мандарини, лимони и грейпфрут) срещу обикновен паяжинообразуващ акар.'),
(7, 'ЕНВИДОР 240 СК', 'СК - суспензионен концентрат', NULL, NULL, 5, 'БАЙЕР Кропсайанс', 'Удостоверение № 01025-ПРЗ/ 17.02.2010 г.', NULL, NULL, NULL, '240 г/л спиродиклофен', '>2500', '3', 6, 'акарицид', 1, 'ЕНВИДОР 240 СК Акарицид срещу червен овощен акар по овощни и жълт лозов акар по лозя.'),
(8, 'ЗУУМ 11 СК', 'СК - суспензионен концентрат', NULL, NULL, 6, 'Сумитомо Кемикъл Агро Юръп САС', 'Разрешение № 01298 – ПРЗ- 2/16.06.2015 г.', '31.07.2018 г.', NULL, NULL, 'eтоксазол 110 г/л', NULL, '3', 8, 'акарицид', 1, 'ЗУУМ 11 СК Акарицид при ябълка, круша, праскова, кайсия, лозя (десертни и винени сортове), домати, дини, пъпеши, памук и др. срещу обикновен паяжинообразуващ акар и червен овощен акар.'),
(9, 'МАСАЙ ВП', 'ВП - намокрим прах', NULL, NULL, 2, 'БАСФ', 'Удостоверение № 01260–ПРЗ/ 11.08.2014 г.', '01.08.2024 г.', NULL, NULL, '200 г/кг тебуфенпирад', NULL, '2', 13, 'акарицид', 1, 'МАСАЙ ВП Инсектицид и акарицид при краставици, пипер, праскови и нектарини, сливи, ябълка и круша, лоза и рози за борба срещу памукова листна въшка,\r\nобикновен паяжинообразуващ акар, червен овощен акар, жълт лозов акар и др.'),
(10, 'МИЛБЕКНОК', 'ЕК - емулсионен концентрат', NULL, NULL, 7, 'Белхим Кроп Протекшън NV/SA', 'Разрешение № 01498 – ПРЗ – 1/07.09.2017 г.', '31.07.2019 г.', 'РД 11- 1770/21.08.2017 г.', NULL, 'милбемектин 9.3 г/л', NULL, '2', 13, 'акарицид', 1, 'МИЛБЕКНОК Акарицид при ябълка, ягоди (полско производство и в оранжерии), мандарина, портокал и декоративни култури срещу червен овощен акар и обикновен паяжинообразуващ акар.'),
(11, 'НАТУРАЛИС', 'ОД - маслена дисперсия', NULL, NULL, 8, 'СБС Европа', 'Разрешение № 01402 – ПРЗ- 1/10.05.2016г.', '30.04.2020 г.', 'РД 11- 692/22.04.2016 г.', NULL, 'Beauveria bassiana, щам ATCC 74040 – 0,185 г/кг', NULL, '2', 14, 'акарицид', 1, 'НАТУРАЛИС Инсектицид и Акарицид при домати, пипер, патладжан, диня, пъпеш,тиква, тиквички, краставици, ягоди, ябълки, круши и лозя – винени и десертри сортове срещу акари, обикновен паяжинообразуващ акар и червен овощен акар и др.'),
(12, 'НИМ АЗАЛ Т/С', 'ЕК - емулсионен концентрат', NULL, NULL, 9, 'Трифолио-М', 'Удостоверение № 71- 3/28.12.2015 г.', NULL, 'РД 11- 1848/09.12.2015 г.', '*Гратисен период 6 месеца за продажба и още 1 година за съхранение и употреба, считано от 09.12.2015 г. на ПРЗ НИМ АЗАЛ Т/С Трифолио-М -Удостоверение № 71- 2/18.05.2011 г.', '1 % азадирахтин А + 0,5 % азадирахтин Б,В,Г, Д + 2,5 % Нимсубстанция', '>5000', '3', 14, 'акарицид', 1, 'НИМ АЗАЛ Т/С Инсектицид и Акарицид при домати и зеленчуци срещу Обикновен паяжинообразуващ акар.'),
(13, 'НИСОРАН 5 ЕК', 'ЕК - емулсионен концентрат', NULL, NULL, 10, 'Нипон Сода', 'Удостоверение № 515/18.10.2007 г.', NULL, NULL, NULL, '50 г/л хекситиазокс', '5000', '3', 14, 'акарицид', 1, 'НИСОРАН 5 ЕК Акарицид с отлично овицидно и ларвицидно действие при голям брой култури (овощни, лозя красравици). Ефикасен срещу всички акари от р. Tetranichus.'),
(14, 'ОВИТЕКС', 'ЕК - емулсионен концентрат', NULL, NULL, 11, 'Кoмптoap Комершъл дес Лубрифиантс', 'Разрешение № 1425-ПРЗ-2/ 07.12.2017 г.', '31.12.2020 г.', 'РД 11- 931/30.05.2016 г. Заповед № РД 11-2558/ 29.11.2017 г.', NULL, 'парафиново масло 817 г/л', NULL, '2', 15, 'акарицид', 1, 'ОВИТЕКС Инсектицид и Акарицид при овощни култури (ябълки, круши, кайсии, череши, праскови, нектарини, сливи и др.), цитруси и декоративни видове срещу щитоносни въшки, акари, листозавивачки и др.'),
